SELECT data_covid.nama, data_covid.tgl_buat, jenis_kasus.jenis_kasus, kabupaten.kabupaten, kecamatan.kecamatan, kelurahan.kelurahan FROM data_covid JOIN jenis_kasus ON data_covid.id_kasus=jenis_kasus.id_kasus JOIN kelurahan ON data_covid.id_kelurahan=kelurahan.kelurahan_id JOIN kecamatan ON kelurahan.kecamatan_id=kecamatan.kecamatan_id JOIN kabupaten ON kecamatan.kabupaten_id=kabupaten.kabupaten_id WHERE data_covid.id_kasus=1